// Basic theme initialization
(function ($) {
    'use strict';

    // Sidebar toggle
    $('#mobile-collapse').on('click', function () {
        $('body').toggleClass('nxl-navigation-toggled');
    });

    $('#menu-mini-button').on('click', function () {
        $('body').toggleClass('nxl-navigation-mini');
        $(this).hide();
        $('#menu-expend-button').show();
    });

    $('#menu-expend-button').on('click', function () {
        $('body').removeClass('nxl-navigation-mini');
        $(this).hide();
        $('#menu-mini-button').show();
    });

    // Dark/Light theme toggle
    $('.dark-button').on('click', function () {
        $('body').removeClass('app-skin-light').addClass('app-skin-dark');
        $(this).hide();
        $('.light-button').show();
    });

    $('.light-button').on('click', function () {
        $('body').removeClass('app-skin-dark').addClass('app-skin-light');
        $(this).hide();
        $('.dark-button').show();
    });

})(jQuery);
